mapper.insert:
insert into board (bno, title, content, writer, view_cnt, delete_yn, insert_time, update_time)
values ( #{bno}
, #{title}
, #{content}
, #{writer}
, 0
, 'N'
, NOW()
, NULL
)

mapper.update:
update board set
    update_time = now(),
    title = ${title},
    content = ${content}
where bno = ${bno}
